import { Card, CardContent, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import "./Counter.css";

function Counter() {
    const [years, setYears] = useState(0);
  const [experts, setExperts] = useState(0);
  const [projects, setProjects] = useState(0);
  const [partners, setPartners] = useState(0);

  const endYears = 6;
  const endExperts = 10;
  const endProjects = 50;
  const endPartners = 20;

  useEffect(() => {
    const interval = setInterval(() => {
      if (years < endYears) {
        setYears(years + 1);
      } else {
        years = 0;
      }

      if (experts < endExperts) {
        setExperts(experts + 1);
      } else {
        experts = 0;
      }

      if (projects < endProjects) {
        setProjects(projects + 1);
      } else {
        projects = 0;
      }

      if (partners < endPartners) {
        setPartners(partners + 1);
      } else {
        partners = 0;
      }
    }, 1000);

    return () => clearInterval(interval);
  }, []);
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        width: "100%",
        marginTop: "89.63px",
      }}
    >
      <Card className="counterCards">
        <CardContent sx={{ textAlign: "center" }}>
          <Typography sx={{ fontSize: "93.06px", fontWeight: "700" }}>
            6
          </Typography>
          <Typography>Years in Field</Typography>
        </CardContent>
      </Card>
      <Card className="counterCards">
        <CardContent sx={{ textAlign: "center" }}>
          <Typography sx={{ fontSize: "93.06px", fontWeight: "700" }}>
            10+
          </Typography>
          <Typography>Expertes</Typography>{" "}
        </CardContent>
      </Card>
      <Card className="counterCards">
        <CardContent sx={{ textAlign: "center" }}>
          <Typography sx={{ fontSize: "93.06px", fontWeight: "700" }}>
            50+
          </Typography>
          <Typography>Prpjects</Typography>{" "}
        </CardContent>
      </Card>
      <Card sx={{ width: "268px", height: "268px" }}>
        <CardContent sx={{ textAlign: "center" }}>
          <Typography sx={{ fontSize: "93.06px", fontWeight: "700" }}>
            20+
          </Typography>
          <Typography>Agency Partners</Typography>{" "}
        </CardContent>
      </Card>
    </div>
  );
}

export default Counter;
